image_width: .byte 55
image_height: .byte 25
pos_x: .byte 0
pos_y: .byte 0 
video_buffer_address: .word 0x1ffffff0  
    
    
.text
main: 
    #store image base address into a0
    la a0, image 
    #store image width into a1
    la t0, image_width
    lbu a1, 0(t0)
    #store image height into a2
    la t0, image_height 
    lbu a2, 0(t0)
    #store x coordinate into a3
    la t0, pos_x
    lbu a3, 0(t0) 
    #store y coordinate into a4
    la t0, pos_y
    lbu a4, 0(t0)
    #unconditional jump and link ra register to label draw_bmp
    call draw_bmp
    call flush_buffer
    call move_bmp
    #unconditional jump to label main
    j main
     
    
    
move_bmp:
    la t0, pos_x
    lb t1, 0(t0)
    addi t1, t1, 1
    sb t1, 0(t0)
    la t0, pos_y
    lb t1, 0(t0)
    addi t1, t1, 1
    sb t1, 0(t0)
    ret

draw_bmp: #usage: draw_bmp(uint32_t &image, uint8_t image_width, uint8_t image_height, uint8_t pos_x, uint8_t pos_y)
    addi sp, sp, -4 
    sw ra, 0(sp)    
    xor s0, s0, s0 #let s0 be the counter for x_loop
    xor s1, s1, s1 #let s1 be the counter for y_loop
    y_loop:
        x_loop:
            call write_to_buffer #a0 holds the address containing colour
                                 #s0 holds the value containing x coordinate
                                 #s1 holds the value containing y coordinate
            addi s0, s0, 1
            addi a0, a0, 4
            blt s0, a1, x_loop
            xor s0, s0, s0
            addi s1, s1, 1
            blt s1, a2, y_loop
            lw ra, 0(sp)
            addi sp, sp, 4
            ret
         
write_to_buffer: 
    addi sp, sp, -16
    sw t0, 12(sp)
    sw t1, 8(sp)
    sw t2, 4(sp)
    sw t3, 0(sp)
    li t1, LED_MATRIX_0_WIDTH
    add s1, s1, a4
    mul t0, s1, t1
    add t0, t0, s0
    add t0, t0, a3
    slli t0, t0, 2
    la t1, video_buffer_address
    lw t1, 0(t1)
    #t0 holds LED coordinate in offset form
    #t1 holds the base address of video buffer
    add t1, t1, t0 
    lw t3, 0(a0)
    sw t3, 0(t1)#store non-zero value at video_buffer[LED_coordinate]
    sub s1, s1, a4
    lw t3, 0(sp)
    lw t2, 4(sp)
    lw t1, 8(sp)
    lw t0, 12(sp)
    addi sp, sp, 16 
    jr ra
    
flush_buffer: 
# for (int i = 0; i < LED_MATRIX_0_SIZE; i++) {
#    LED_MATRIX_O[i] = video_buffer[i]; video_buffer[i] = colour:
#    video_buffer[i] = 0;
#}
    la t0, video_buffer_address
    lw t0, 0(t0)
    li t1, LED_MATRIX_0_BASE
    li t3, LED_MATRIX_0_SIZE
    xor t2, t2, t2
    flush_loop:
        add s0, t0, t2
        lw s2, 0(s0)
        add s1, t1, t2
        sw s2, 0(s1)
        sw zero, 0(s0)
        beq t2, t3, exit_flush_loop
        addi t2, t2, 4
        j flush_loop
        exit_flush_loop:
        jr ra
